const [notes, setNotes] = useState([]);

  function addNote(newNote){
setNotes(x=>{
  return [...x,newNote];
})
  }
  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote} />
      <Note key={1} title="Note title" content="Note content" />
      <Footer />
    </div>
  );
}

export default App;
